package otherPrograms;

public class PrimeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 101;

		if (number % 2 == 0) {
			System.out.println("No is prime: " + number);
		}

		else {
			System.out.println("No is not prime: " + number);
		}
	}

}
